import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import usersData from '../../users.json';
import './Parallax/parallax.css'

const LoginPage = ({ usersData }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailDirty, setEmailDirty] = useState(false);
  const [passwordDirty, setPasswordDirty] = useState(false);
  const [emailError, setEmailError] = useState('Email cannot be empty');
  const [passwordError, setPasswordError] = useState('Password cannot be empty');
  const [formValid, setFormValid] = useState(false);
  const [user, setUser] = useState(null); // Стан для авторизованого користувача

  const emailHandler = (e) => {
    setEmail(e.target.value);
  };

  const passwordHandler = (e) => {
    setPassword(e.target.value);
  };

  const blurHandler = (e) => {
    if (e.target.name === 'email') {
      setEmailDirty(true);
    } else if (e.target.name === 'password') {
      setPasswordDirty(true);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    const foundUser = usersData.find((user) => user.email === email && user.password === password);
    
    if (foundUser) {
      setUser(foundUser); // Зберігаємо користувача у стані
    } else {
      alert('Невірний email або пароль');
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={submitHandler} className='login-form'>
        <h1>Login</h1>

        <input
          onChange={emailHandler}
          value={email}
          onBlur={blurHandler}
          name='email'
          type="text"
          placeholder='Enter your email...'
        />
        {(emailDirty && emailError) && <div style={{ color: 'red' }}>{emailError}</div>}

        <input
          onChange={passwordHandler}
          value={password}
          onBlur={blurHandler}
          name='password'
          type="password"
          placeholder='Enter your password...'
        />
        {(passwordDirty && passwordError) && <div style={{ color: 'red' }}>{passwordError}</div>}

        <button disabled={!formValid} type='submit'>
          Login
        </button>
      </form>

      {/* Якщо користувач авторизований, передаємо його дані в SideBar */}
      {user && <SideBar user={user} />}
    </div>
  );
};

export default <LoginForm></LoginForm>;
