import React from 'react'
import LoginForm from '../LoginForm.jsx'
import './parallax.css'

const Parallax = () => {
  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = e.pageX / $(window).width(); // Виправлено на pageX
      const y = e.pageY / $(window).height(); // Виправлено на pageY

      $('.mouse-parallax_background').css(
        'transform',
        'translate(-' + x * 30 + 'px, -' + y * 30 + 'px)'
      );

      $('.mouse-parallax_background').css(
        'transform',
        'translate(' + x * 60 + 'px, ' + y * 60 + 'px)'
      );
    };


	return(
		<>
		<div className='mouse-parallax'>
			<div className="mouse-parallax_background">
			</div>
		</div>
	</>
	)
})
}
export default Parallax;
