import React, { Component, useState } from 'react'

const Form = () => {
	const [email, setEmail] = useState ('')
	const [password, setPassword] = useState('')
	const [emailDirty, setEmailDirty] = useState(false)
	const [passwordDirty, setPasswordDirty] = useState(false)
	const [emailError, setEmailError] = useState('Рядок не може бути пустим')
	const [passwordError, setPasswordError] = useState('Рядок не може бути пустим')

	const emailHandler  = (e) => {
		setEmail(e.tar)
	}

	const blurHandler = (e) => {
		switch (e.target.name){
			case 'email':
				setEmailDirty (true)
				break
			case 'password':
				setPasswordDirty (true)	
				break
		}
	}

		return (
			<div className='container'>
				<form>
					<h1>Login</h1>
					{(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
				
					<input onBlur={e => blurHandler(e)} name='email' type="text" placeholder='Enter your email...' />
					{(passwordDirty && passwordError) && <div style={{color: 'red'}}>{passwordError}</div>}
					<input onBlur={e => blurHandler(e)} name='password' type="password" placeholder='Enter your password...' />
					<button type='submit'>Login</button>
				</form>
			</div>
		)
	}
	export default Form
